import React, { useEffect, useState} from 'react';
import axios from 'axios';
import {Link, useNavigate, useParams} from 'react-router-dom'
import {faClose, faPen} from "@fortawesome/free-solid-svg-icons"
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome"

function UpdateMed() {
    const [nom, setNom] = useState('')
    const [nombreDeJour, setNombreDeJour] = useState('')
    const [tauxJournalier, setTauxJournalier] = useState('')
    const {id} = useParams()
    const navigate = useNavigate('');

    useEffect(() => {
        async function fetchData() {
            const res = await axios.get('http://localhost:8081/update/${id}');
            if(res.status === 200){
                setNom(res.data[0].nom);
                setNombreDeJour(res.data[0].nombreDeJour);
                setTauxJournalier(res.data[0].tauxJournalier);
            }
        }
        fetchData();
    }, [id]);

    function handleModif(event){
        event.preventDefault();
        axios.put('http://localhost:8081/update/'+id,{nom,nombreDeJour,tauxJournalier})
        .then(res =>{
            console.log(res);
            navigate('/');
        }).catch(err => console.log(err));
    }

  return (
    <div className='d-flex vh-100 bg-white justify-content-center align-items-center'>
      <div className='w-45 bg-light rounded p-3'>
                <form onSubmit={handleModif}>
                    <h2>Enregitrer l'Info Medicin</h2>
                    <div className='mb-2'>
                        <label htmlFor=''>Nom</label>
                        <input type='text' placeholder='Entrer votre Nom' 
                        required className='form-control'
                        onChange={e => setNom(e.target.value)}/>
                    </div>
                    <div className='mb-2'>
                        <label htmlFor=''>Nombre de Jour</label>
                        <input type='number' placeholder='Entrer le Nombre de jour' 
                        required className='form-control'
                        onChange={e => setNombreDeJour(e.target.value)}/>
                    </div>
                    <div className='mb-2'>
                        <label htmlFor=''>Taux Journalier</label>
                        <input type='number' placeholder='Entrer le Taux Journalier'
                         required className='form-control'
                        onChange={e => setTauxJournalier(e.target.value)}/>
                    </div>
                    <button className='btn btn-primary'><FontAwesomeIcon icon={faPen}/></button>
                    <Link to='/' className='btn  btn-danger m-3'><FontAwesomeIcon icon={faClose}/></Link>
                </form>
            </div>
    </div>
  )
}

export default UpdateMed
